			}
		} else {
			for (Node<E> node = first; node != null; node = node.next) {
				if (obj.equals(node.data)) {
					return index;
				}
				index++;
			}
		}
		return -1;
	}

	// 
	public int lastIndexOf(Object obj) {
		int index = 0;
		int i = 0;
		int[] array = new int[size];
		if (obj == null) {
			for (Node<E> node = first; node != null; node = node.next) {
				if (node.data == null) {
					array[i++] = index;
				}
				index++;
			}
		} else {
			for (Node<E> node = first; node != null; node = node.next) {
				if (obj.equals(node.data)) {
					array[i++] = index;
				}
				index++;
			}
		}
		if (array.length != 0) {
			return array[i - 1];
		}
		return -1;
	}

