E unlink(Node<E> x) {
	final E element = x.item;
	final Node<E> next = x.next;
	final Node<E> prev = x.prev;

	/* [===] >  [===]  > [===] 
	 *  ||	                   ||
	 * null                  first
	 */
	if (prev == null) {
		first = next;
	} else {
		prev.next = next;
		x.prev = null;
	}	

	/* [===] >  [===]  > [===] 
	 *  ||                    ||
	 * last                  null  
	 */
	if (next == null) {
		last = prev;
	} else {
		next.prev = prev;
		x.next = null;
	}

	x.item = null;
	size--;
	modCount++;
	return element;
}
